/**
 * Basic input component
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const Input = (props) => {
	function changeHandler(event){
		return props.onValueChange(event.target.value);
	}
	return (
		<input
			type={props.inputType}
			id={props.id}
			className={props.fieldClassName}
			aria-describedby={props.ariaDescribedbyAttr}
			required={props.isRequired}
			onChange={changeHandler}
			value={props.value}
		/>
	);

};

/**
 * Prop definitions for Input components
 */
Input.propTypes = fieldPropTypes;

/**
 * Default props for Input Component
 *
 * @type {{ariaDescribedbyAttr: string, isRequired: boolean, inputType: string}}
 */
Input.defaultProps = {
	ariaDescribedbyAttr: '',
	isRequired: false,
	inputType: 'text'
};

import PropTypes from 'prop-types';

/**
 * PropType for the field value used in multiple components
 *
 * @type {shim}
 */
export const valuePropType = PropTypes.oneOfType([
	PropTypes.string,
	PropTypes.number,
	PropTypes.array
]);

/**
 * PropType for the field value change event used in multiple components
 *
 * @type {shim}
 */
export const onValueChangePropType = PropTypes.func.isRequired;

/**
 * Proptypes for Input component
 * @type {shim}
 */
export  const inputTypeProp = PropTypes.string;

/**
 * PropTypes for field groups
 *
 * @type {{id: (boolean|shim|*), isBlockInput: shim, isRequired: shim, help: shim, label: (boolean|shim|*), type: shim, value: shim, onValueChange: (boolean|shim|*), inputType: shim}}
 */
export const fieldGroupPropTypes = {
	id: PropTypes.string.isRequired,
	isBlockInput: PropTypes.bool,
	isRequired: PropTypes.bool,
	help: PropTypes.string,
	label: PropTypes.string.isRequired,
	type: PropTypes.oneOf(['input']),
	value: valuePropType,
	onValueChange: onValueChangePropType,
	inputType:inputTypeProp,
};

/**
 * PropTypes for the InnerField component
 *
 * @type {{id: (boolean|shim|*), fieldClassName: (boolean|shim|*), help: shim, value: shim, onValueChange: (boolean|shim|*), inputType: shim}}
 */
export const fieldInnerPropTypes = {
	id: PropTypes.string.isRequired,
	fieldClassName: PropTypes.string.isRequired,
	help: PropTypes.string,
	value: valuePropType,
	onValueChange: onValueChangePropType,
	inputType: inputTypeProp
};

export const fieldPropTypes = {
	...fieldInnerPropTypes,
	options: PropTypes.array,
	ariaDescribedbyAttr: PropTypes.string,
};

import React from 'react';
import Downshift from 'downshift';
import PropTypes from 'prop-types';
import {fieldPropTypes} from '../propTypes';

/**
 * Creates a select field
 *
 * Wraps [downshift](https://github.com/paypal/downshift)
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const SelectField = (props) => {
	/**
	 * Dispatches new value(s) to parent
	 *
	 * @param selection
	 * @returns {*}
	 */
	function changeHandler(selection) {
		return props.onValueChange(selection);
	}

	return (
		<Downshift
			inputValue={props.value}
			defaultInputValue={props.value}
			onChange={changeHandler}
			itemToString={item => (item ? item.value : '')}
			defaultIsOpen={props.isOpen}
		>
			{({
				getInputProps,
				getItemProps,
				isOpen,
				inputValue,
				highlightedIndex,
				selectedItem,
			}) => (
				<div>
					<input {...getInputProps({
						id: props.id
					})} />
					{isOpen ? (
						<div>
							{props.options
								.filter(item => !inputValue || item.value.includes(inputValue))
								.map((item, index) => (
									<div
										key={item.value}
										{...getItemProps({
											key: item.value,
											index,
											item,
											style: {
												backgroundColor:
													highlightedIndex === index ? 'lightgray' : 'white',
												fontWeight: selectedItem === item ? 'bold' : 'normal',
											},
											className: 'caldera-config-option'
										})}
									>
										{item.value}
									</div>
								))}
						</div>
					) : null}
				</div>
			)}
		</Downshift>
	);
};


SelectField.propTypes = {
	...fieldPropTypes,
	isOpen: PropTypes.bool,
};

/**
 * Create the aria-describedby attribute or id attribute for the describing element
 *
 * @param {String} id Id attribute of input being described
 * @param {String} help Optional. Help text. If empty return is null.
 * @returns {String|null}
 */
export const  ariaDescribedbyAttr = (id, help = '') => {
	let ariaDescribedby = null;
	if (help.length) {
		ariaDescribedby = `${id}-description`;
	}
	return ariaDescribedby;
};


/**
 * Get HTML5 input types that are valid
 * @returns {string[]}
 */
export function getHtmlInputTypes() {
	return [
		'text',
		'email',
		'number',
		'date',
		'datetime',
		'password',
		'submit',
		'reset',
		'checkbox',
		'hidden'
	];
}

/**
 * Checks if a given input type is an acceptable HTML5 input type
 *
 * @param {String} type
 * @returns {boolean}
 */
export function isValidHtml5type(type) {
	return getHtmlInputTypes().includes(type);
}

