	'type': 'dropdown',
	'label': 'Content type',
	'description': 'Choose content type, default is HTML',
	options: [
		{
			label: 'HTML',
			value: 'html'
		},
		{
			label: 'Plain Text',
			value: 'plain'
		}
	],
	value: selectFieldValue,
	onValueChange: function(newValue){
		selectFieldValue = newValue
	}
};

let fieldSetFieldValue = ['1'];
const fieldSetField = {
	id: 'fieldset-3',
	label: 'How many?',
	type: 'fieldset',
	options: [
		{
			value: '1',
			label: 'One'
		},
		{
			value: '2',
			label: 'Two'
		}

	],
	value:[],
	onValueChange:(newValue) => {
		fieldSetFieldValue=newValue;
