import React from 'react';
import propTypes from 'prop-types';
import {fieldSetFactory} from './fields/factories/fieldSetFactory';

/**
 * Component for rendering an array of field configs
 */
export class RenderGroup extends React.Component {


	/**
	 * Constructor for RenderGroup component
	 * @param {Object} props
	 */
	constructor(props){
		super(props);
		this.createComponents = this.createComponents.bind(this);


	}

	/**
	 * Create components
	 *
	 * @return {Array}
	 */
	createComponents(){
		return fieldSetFactory(this.props.configFields);
	}

	/**
	 * Renderer for RenderGroup component
	 *
	 * @return {*}
	 */
	render(){
		return(
			<div
				className={this.props.className ? this.props.className : RenderGroup.classNames.renderGroupWrapper }
			>
				{this.createComponents().map((configField,i) => {
					return React.createElement(
						'div', {
							key: i,
							className: RenderGroup.classNames.fieldGroup
						},
						configField
					);
				})}
			</div>
		);
	}
}

/**
 * Prop definitions for RenderGroup component
 * @type {{configFields: *, className: shim}}
 */
RenderGroup.propTypes = {
	configFields: propTypes.array.isRequired,
	className: propTypes.string
};

/**
 * Classnames for elements (wrappers, field groups and form fields)
 *
 * @type {{renderGroupWrapper: string, fieldWrapper: string}}
 */
RenderGroup.classNames = {
	renderGroupWrapper: 'caldera-config-field-setup',
	fieldGroup: 'caldera-config-group',
	fieldWrapper: 'caldera-config-field',
	input: 'field-config',

};

import React from 'react';
import classNames from 'classnames';
import {ariaDescribedbyAttr} from './util';
import {
	fieldGroupPropTypes,
} from './propTypes';

import {FieldInner} from './FieldInner';
import {RenderGroup} from '../RenderGroup';
import {Message} from './messages/Message';
import {messageObjectFactory} from './messages/messageObjectFactory';
import {fieldsetCheckboxHandler} from './field-group-change-handlers/fieldsetCheckboxHandler';

/**
 * Represents one configField -- wrapper, label and input.
 *
 * @param props
 * @return {*}
 * @constructor
 */
export const FieldGroup = (props) => {

	/**
	 * Creates the id attribute
	 * @return {String}
	 */
	function idAttrForHelpElement() {
		return ariaDescribedbyAttr(props.id, props.help);
	}


	/**
	 * Based on type, render the field.
	 *
	 * @return {*}
	 */
	function fieldInner(fieldProps) {
		const message = 'object' === typeof  fieldProps.message
			? messageObjectFactory(fieldProps.message)
			: messageObjectFactory({message: null, error: false});

		function getFieldClassName(conditionalClassNames) {
			return classNames(
				{
					required: fieldProps.isRequired,
					...conditionalClassNames
				}
			);
		}

		/**
		 * Is this option the checked value?
		 * @param {object}option
		 * @param {String|number|Array} currentValue
		 * @return {boolean}
		 */
		function isCheckedOption(option, currentValue) {
			if (Array.isArray(currentValue)) {
				return currentValue.includes(option.value);
			}
			return option.value === currentValue;
		}

		//Fieldsets are rendered recursively.
		if ('fieldset' === fieldProps.type) {
			return (
				<fieldset
					id={props.id}
				>
					<legend>
						{props.label}
					</legend>
					{props.options.map(option => {


						//Call this same function, as a regular checkbox
						return (
							<React.Fragment
								key={`${props.id}-${option.value}`}
							>
								{fieldInner({
									type: 'input',
									inputType: 'checkbox',
									id: `${props.id}-${option.value}`,
									value: isCheckedOption(option, props.value),
									fieldClassName: getFieldClassName({
										'fieldset-checkbox': true,
									}),
									label: option.label,
									disabled: props.disabled,
									onValueChange: () => fieldsetCheckboxHandler(option, props.values, props.onValueChange)
								})}
							</React.Fragment>
						);
					})}
				</fieldset>
			);
		} else if ('hidden' === fieldProps.inputType) {
			return (
				FieldInner(
					{
						type: props.type,
						id: props.id,
						fieldClassName: getFieldClassName(),
						value: fieldProps.value,
						onValueChange: fieldProps.onValueChange,
						inputType: fieldProps.inputType,
						disabled: props.disabled,
					}
				)
			);
		} else {
			return (
				<div
					className={RenderGroup.classNames.fieldWrapper}
				>
					<label
						htmlFor={fieldProps.id}
					>
						{fieldProps.label}
					</label>
					<Message
						message={message}
					/>
					{FieldInner(
						{
							type: fieldProps.type,
							id: fieldProps.id,
							fieldClassName: getFieldClassName({
								'block-input': fieldProps.isBlockInput
							}),
							help: fieldProps.help,
							value: fieldProps.value,
							onValueChange: fieldProps.onValueChange,
							inputType: fieldProps.inputType,
							options: fieldProps.options,
							disabled: props.disabled,
						}
					)}
					{fieldProps.help &&
					<p
						id={`${idAttrForHelpElement()}`}
						className={'description'}
					>
						{fieldProps.help}
					</p>
					}
				</div>
			);
		}

	}

	/**
	 * Render field group
	 */
	return fieldInner(props);
};


FieldGroup.propTypes = fieldGroupPropTypes;

FieldGroup.defaultProps = {
	isBlockInput: true,
	isRequired: false,
	help: ''
};

import {fieldPropTypes} from './propTypes';
import React from 'react';
import {Input} from './input/Input';
import {SelectField} from './select/SelectField';
import {ariaDescribedbyAttr} from './util';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {RenderGroup} from '../RenderGroup';
/**
 * Creates the field inside of a field group
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const FieldInner = (props) => {

	/**
	 * Get the ID for the description element if it will be created
	 *
	 * @return {String|null}
	 */
	function ariaIdAttr() {
		return ariaDescribedbyAttr(props.id, props.help);
	}

	/**
	 * Get the className prop for the
	 *
	 * @return {String}
	 */
	function inputClassName() {
		return classNames([
			props.fieldClassName,
			RenderGroup.classNames.input
		]
		);
	}

	switch( props.type ){
	case 'select':
	case 'dropdown':
		return (
			<SelectField
				id={props.id}
				fieldClassName={inputClassName()}
				ariaDescribedbyAttr={ariaIdAttr()}
				value={props.value}
				onValueChange={props.onValueChange}
				inputType={props.inputType}
				options={props.options}
				disabled={props.disabled}
			/>
		);
	default:
	case 'input':
		return (
			<Input
				id={props.id}
				fieldClassName={inputClassName()}
				ariaDescribedbyAttr={ariaIdAttr()}
				value={props.value}
				onValueChange={props.onValueChange}
				inputType={props.inputType}
				disabled={props.disabled}
			/>);
	}

};

/**
 * propTypes for FieldInner component
 *
 * @type {{options, ariaDescribedbyAttr}}
 */
FieldInner.propTypes = {
	...fieldPropTypes,
	type: PropTypes.string,
};

/**
 * Default props for FieldInner component
 * @type {{help: string}}
 */
FieldInner.defaultProps = {
	help: '',
	type: 'input'
};

import {prepareFieldConfig} from './prepareFieldConfig';
import {FieldGroup} from '../FieldGroup';


/**
 * Generates field controls
 *
 * @param {Array} fieldArgs Field config
 * @returns {*}
 */
export const fieldFactory = (fieldArgs) =>{
	return FieldGroup(prepareFieldConfig(fieldArgs));
};

import {fieldFactory} from './fieldFactory';

/**
 * Given an array of field configs, generates and array of field controls
 *
 * Should be used for a group of settings, such as all settings for a processor.
 *
 * @param {Array} fields Field configs
 * @returns {Array}
 */
export const fieldSetFactory = (fields) =>{
	let out = [];
	Object.values(fields).forEach( field => {
		out.push(fieldFactory(field));
	});
	return out;
};

