import React from 'react';
import {fieldPropTypes} from '../propTypes';
import classNames from 'classnames';
import {RenderGroup} from '../../RenderGroup';
/**
 * Input component for any HTML5 input type
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const Input = (props) => {
	/**
	 * Dispatches value of input when it changes
	 * @param event
	 * @return {*}
	 */
	function changeHandler(event){
		return props.onValueChange(event.target.value);
	}

	/**
	 * Dispatches value when checkbox is checked
	 * @return {*}
	 */
	function checkboxChangeHandler() {
		return props.onValueChange(! props.value);
	}


	/**
	 * Get the className prop for inputs
	 *
	 * @return {String}
	 */
	function inputClassName() {
		return classNames(
			props.fieldClassName,
			RenderGroup.classNames.input
		);
	}

	if( 'checkbox' === props.inputType ){
		return (
			<input
				type={'checkbox'}
				id={props.id}
				className={inputClassName()}
				aria-describedby={props.ariaDescribedbyAttr}
				required={props.isRequired}
				onChange={checkboxChangeHandler}
				defaultChecked={props.value}
				disabled={props.disabled}
			/>
		);
	}

	return (
		<input
			type={props.inputType}
			id={props.id}
			className={inputClassName()}
			aria-describedby={props.ariaDescribedbyAttr}
			required={props.isRequired}
			onChange={changeHandler}
			value={props.value}
			disabled={props.disabled}
		/>
	);

};

/**
 * Prop definitions for Input components
 */
Input.propTypes = fieldPropTypes;

/**
 * Default props for Input Component
 *
 * @type {{ariaDescribedbyAttr: string, isRequired: boolean, inputType: string}}
 */
Input.defaultProps = {
	ariaDescribedbyAttr: '',
	isRequired: false,
	inputType: 'text'
};

import React from 'react';
import PropTypes from 'prop-types';
import {messagePropShape} from './messagePropTypes';
import classNames from 'classnames';

/**
 * The class that message components always wrap messages in
 * @type {string}
 */
export const MESSAGE_CLASS = 'caldera-components-message';

/**
 * Prop for showing a validation message
 *
 * Use or for errors and such
 *
 * @param {object}  props
 * @return {*}
 * @constructor
 */
export const Message = (props) => {
	const {message,error} = props.message;
	if( !message || '' === message ){
		return <React.Fragment></React.Fragment>;
	}
	return(
		<div
			className={classNames(
				props.className,
				MESSAGE_CLASS,
				{
					'has-error': error,
					'caldera-components-error': error,
					'caldera-components-not-error': ! error,

				}
			)}
		>
			{message}
		</div>
	);
};

/**
 * Prop types for Message components
 *
 * @type {{className: shim, message}}
 */
Message.propTypes = {
	className: PropTypes.string,
	message: messagePropShape,
};

import {toBoolean} from '../util';

/**
 * Prepares messages object in fieldConfigs
 *
 * @param {Object} message
 * @param {Boolean} error
 * @return {{message: *, error: boolean}}
 */
export const messageObjectFactory = ({message,error}) => {
	if( 'string' !== typeof  message ){
		message = '';
	}
	return{
		message: message,
		error: toBoolean(error)
	};
};

import PropTypes from 'prop-types';

/**
 * The shape of the message object passed to Message compoent
 *
 * @type {shim}
 */
export const messagePropShape = PropTypes.shape(
	{
		error: PropTypes.bool,
		text: PropTypes.string
	}
);

import PropTypes from 'prop-types';
import {messagePropShape} from './messages/messagePropTypes';

/**
 * PropType for the field value used in multiple components
 *
 * @type {shim}
 */
export const valuePropType = PropTypes.oneOfType([
	PropTypes.string,
	PropTypes.number,
	PropTypes.array,
	PropTypes.bool
]);

/**
 * PropType for the field value change event used in multiple components
 *
 * @type {shim}
 */
export const onValueChangePropType = PropTypes.func.isRequired;

/**
 * Proptypes for Input component
 * @type {shim}
 */
export  const inputTypeProp = PropTypes.string;

/**
 * PropTypes for field groups
 *
 * @type {{id: (boolean|shim|*), isBlockInput: shim, isRequired: shim, help: shim, label: (boolean|shim|*), type: shim, value: shim, onValueChange: (boolean|shim|*), inputType: shim}}
 */
export const fieldGroupPropTypes = {
	id: PropTypes.string.isRequired,
	isBlockInput: PropTypes.bool,
	isRequired: PropTypes.bool,
	help: PropTypes.string,
	label: PropTypes.string.isRequired,
	type: PropTypes.oneOf(['input','select', 'fieldset']),
	value: valuePropType,
	onValueChange: onValueChangePropType,
	options: PropTypes.array,
	inputType:inputTypeProp,
	disabled: PropTypes.bool,
	message: messagePropShape
};

/**
 * PropTypes for the InnerField component
 *
 * @type {{id: (boolean|shim|*), fieldClassName: (boolean|shim|*), help: shim, value: shim, onValueChange: (boolean|shim|*), inputType: shim}}
 */
export const fieldInnerPropTypes = {
	id: PropTypes.string.isRequired,
	fieldClassName: PropTypes.string.isRequired,
	help: PropTypes.string,
	value: valuePropType,
	onValueChange: onValueChangePropType,
	inputType: inputTypeProp,
};

export const fieldPropTypes = {
	...fieldInnerPropTypes,
	ariaDescribedbyAttr: PropTypes.string,
};

import React from 'react';
import {fieldPropTypes} from '../propTypes';
import classNames from 'classnames';
import {RenderGroup} from '../../RenderGroup';

/**
 * Creates a select field
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const SelectField = (props) => {
	/**
	 * Dispatches new value(s) to parent
	 *
	 * @param selection
	 * @returns {*}
	 */
	function changeHandler(selection) {
		return props.onValueChange(selection);
	}

	/**
	 * Get the className prop for the select element
	 *
	 * @return {String}
	 */
	function selectClassNames() {
		return classNames(
			props.fieldClassName,
			RenderGroup.classNames.input
		);
	}

	return (
		<select
			id={props.id}
			className={selectClassNames()}
			value={props.value}
			disabled={props.disabled}
			onChange={changeHandler}
		>
			{props.options
				.map((item,i) => {
					const key = 'string' === typeof item.value ? item.value : i;
					return(
						<option
							key={key}
							className={ 'caldera-config-option' }
							value={item.value}
						>
							{item.label}
						</option>
					);
				})
			}
		</select>



	);
};

/**
 * Prop definition for select fields
 *
 * @type {{}}
 */
SelectField.propTypes = {
	...fieldPropTypes,
