import React from 'react';
import Downshift from 'downshift';
import PropTypes from 'prop-types';
import {fieldPropTypes} from '../propTypes';

/**
 * Creates a select field
 *
 * Wraps [downshift](https://github.com/paypal/downshift)
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const SelectFieldFancy = (props) => {
	/**
	 * Dispatches new value(s) to parent
	 *
	 * @param selection
	 * @returns {*}
	 */
	function changeHandler(selection) {
		return props.onValueChange(selection);
	}

	return (
		<Downshift
			inputValue={props.value}
			defaultInputValue={props.value}
			onChange={changeHandler}
			itemToString={item => (item ? item.value : '')}
			defaultIsOpen={props.isOpen}
		>
			{({
				getInputProps,
				getItemProps,
				isOpen,
				inputValue,
				highlightedIndex,
				selectedItem,
			}) => (
				<div>
					<input {...getInputProps({
						id: props.id
					})} />
					{isOpen ? (
						<div>
							{props.options
								.filter(item => !inputValue || item.value.includes(inputValue))
								.map((item, index) => (
									<div
										key={item.value}
										{...getItemProps({
											key: item.value,
											index,
											item,
											style: {
												backgroundColor:
													highlightedIndex === index ? 'lightgray' : 'white',
												fontWeight: selectedItem === item ? 'bold' : 'normal',
											},
											className: 'caldera-config-option'
										})}
									>
										{item.value}
									</div>
								))}
						</div>
					) : null}
				</div>
			)}
		</Downshift>
	);
};


SelectFieldFancy.propTypes = {
	...fieldPropTypes,
	isOpen: PropTypes.bool,
};

/**
 * Create the aria-describedby attribute or id attribute for the describing element
 *
 * @param {String} id Id attribute of input being described
 * @param {String} help Optional. Help text. If empty return is null.
 * @returns {String|null}
 */
export const  ariaDescribedbyAttr = (id, help = '') => {
	let ariaDescribedby = null;
	if (help.length) {
		ariaDescribedby = `${id}-description`;
	}
	return ariaDescribedby;
};


/**
 * Get HTML5 input types that are valid
 * @returns {string[]}
 */
export function getHtmlInputTypes() {
	return [
		'text',
		'email',
		'number',
		'date',
		'datetime',
		'password',
		'submit',
		'reset',
		'checkbox',
		'hidden'
	];
}

/**
 * Checks if a given input type is an acceptable HTML5 input type
 *
 * @param {String} type
 * @returns {boolean}
 */
export function isValidHtml5type(type) {
	return getHtmlInputTypes().includes(type);
}


/**
 * Remove a value from an array if present, if not present, add it
 *
 * @param {String|number} value Value to add or remove
 * @param {Array} array Array to mutate
 * @return {*}
 */
export const addOrRemoveFromArray = (value,array) => {
	const index = array.indexOf(value);
	if (index !== -1){
		array.splice(index, 1);
	}else{
		array.push( value );
	}
	return array;
};

/**
 * Cast a boolean or boolean like to a true or false
 *
 * @param Mixed} value Value to cast
 * @return {boolean}
 */
export const toBoolean =(value) => {
	switch(value){
	case true:
	case 'true':
	case 1:
	case '1':
	case 'on':
	case 'yes':
		return true;
	default:
		return false;
	}
};

