import {checkConfigFieldConditionals} from './checkConfigFieldConditionals';
import {checkConfigFieldsConditionals} from './checkConfigFieldsConditionals';
import {reduceConfigFieldsToValues} from './util';

/**
 * The Caldera Components conditional logic system
 */
export default {
	checkConfigFieldConditionals,
	checkConfigFieldsConditionals,
	reduceConfigFieldsToValues
};

/**
 * Get the current values for prepareConfigFields
 *
 * @param {Array} configFields
 */
export const reduceConfigFieldsToValues = (configFields) => {
	let values = {};
	configFields.forEach(field => {
		values[field.ID] = field.value
			? field.value
			: field.default
				? field.default
				: null;
	});
	return values;
};

import React from 'react';
import {optionShape} from '../propTypes';
import PropTypes from 'prop-types';
/**
 * Creates a button group field
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const ButtonGroup = (props) => {
	/**
	 * Dispatches new value(s) to parent
	 *
	 * @param selection
	 * @returns {*}
	 */
	function changeHandler(selection) {
		return props.onChange(selection);
	}

	/**
	 * Render a button group
	 */
	return (
		<div
			role="group"
		>
			{props.options.map(option => {
				return <button
					aria-label={option.ariaLabel ? option.ariaLabel : option.label}
					key={option.value}
					className={props.value === option.value ? 'selected' : 'not-selected' }
					onClick={() => {
						return changeHandler(option.value);
					}}
				>
					{option.hasOwnProperty('icon')&&
						<span className={option.icon}></span>
					}
					{!option.hasOwnProperty('icon') &&
						<React.Fragment>{option.label}</React.Fragment>
					}

				</button>;
			})}
		</div>
	);
};

/**
 * Prop definition for select fields
 *
 * @type {{}}
 */
ButtonGroup.propTypes = {
	onChange: PropTypes.func.isRequired,
	options: PropTypes.arrayOf(
		PropTypes.shape({
			...optionShape,
			icon:PropTypes.string,
			ariaLabel:PropTypes.string
		})
	),
	value: PropTypes.oneOfType([
		PropTypes.number,
		PropTypes.string
	])
};

/**
 * Default props for select fields
 *
 * @type {{options: Array}}
 */
ButtonGroup.defaultProps = {
	options: []
};

import {prepareFieldConfig} from './prepareFieldConfig';
import {FieldGroup} from '../FieldGroup';


/**
 * Generates field controls
 *
 * @param {Object} fieldArgs Field config
 * @returns {*}
 */
export const fieldFactory = (fieldArgs) =>{
	return FieldGroup(prepareFieldConfig(fieldArgs));
};

import {fieldFactory} from './fieldFactory';

/**
 * Given an array of field configs, generates and array of field controls
 *
 * Should be used for a group of settings, such as all settings for a processor.
 *
 * @param {Array} fields Field configs
 * @returns {Array}
 */
export const fieldSetFactory = (fields) =>{
	let out = [];
	Object.values(fields).forEach( field => {
		out.push(fieldFactory(field));
	});
	return out;
};

/*eslint no-undef: "error"*/
/*eslint-env node*/
import {fieldFactory} from './fieldFactory';
import {fieldSetFactory} from './fieldSetFactory';

module.exports = {
	fieldFactory,
	fieldSetFactory
};

import {fieldGroupPropTypes,magicGroupPropTypes} from '../propTypes';
import {isValidHtml5type, toBoolean} from '../util';
import {messageObjectFactory} from '../messages/messageObjectFactory';

/**
 * Validates field configurations
 *
 * NOTE: This is called internally by factory. You probably do not want to call this.
 *
 * @param {Object}fieldArgs
 * @returns {*}
 */
export const prepareFieldConfig = (fieldArgs) => {
	/**
	 * Pick whitelisted keys from object
	 *
	 * @see http://www.jstips.co/en/javascript/picking-and-rejecting-object-properties/
	 *
	 * @param {Object} obj The object to pick from
	 * @param {Array} keys The whitelist of keys
	 * @return {*}
	 */
	function pick(obj, keys) {
		return keys.map(k => k in obj ? {[k]: obj[k]} : {})
			.reduce((res, o) => Object.assign(res, o), {});
	}

	switch (fieldArgs.type) {
	case 'select':
	case 'dropdown':
		fieldArgs.type = 'select';
		break;
	case 'fieldset':
		fieldArgs.type = 'fieldset';
		break;
	case 'text':
	case 'number':
	default:
		fieldArgs.inputType = isValidHtml5type(fieldArgs.type) ? fieldArgs.type : 'text';
		fieldArgs.type = 'input';
		break;
	}

	if (fieldArgs.hasOwnProperty('desc')) {
		fieldArgs.help = fieldArgs.desc;
	}
	if (fieldArgs.hasOwnProperty('description')) {
		fieldArgs.help = fieldArgs.description;
	}

	let validators = [];
	if( fieldArgs.hasOwnProperty('validators') && Array.isArray(fieldArgs.validators )){
		validators = fieldArgs.validators;
	}

	const keys = 'magic' === fieldArgs.type  ? magicGroupPropTypes : fieldGroupPropTypes;
	fieldArgs = pick(fieldArgs, Object.keys(keys));
	fieldArgs.disabled = toBoolean(fieldArgs.disabled);
	fieldArgs.message = 'object' === typeof  fieldArgs.message
		? messageObjectFactory(fieldArgs.message)
		: messageObjectFactory({message:null, error: false });

	fieldArgs.validators = validators;
	return fieldArgs;
};

import {addOrRemoveFromArray} from '../util';

/**
 * Change handler for a checkbox that is a part of a group
 * @param {Object} option Option that is being changed. Shape: `{value:String|number,label:String}`,
 * @param {Array|string|number} values Current values
 * @param {Function} onValueChange Function to call with updated value
 */
export const fieldsetCheckboxHandler = (option, values, onValueChange)  => {
	if (!Array.isArray(values)) {
		if ('string' === typeof values || 'number' === typeof  values) {
			values = [values];
		} else {
			values = [];

		}
	}
	values = addOrRemoveFromArray(option.value, values);
	onValueChange(values);
};

import React from 'react';
import classNames from 'classnames';
import {ariaDescribedbyAttr} from './util';
import {
	fieldGroupPropTypes,
} from './propTypes';

import {FieldInner} from './FieldInner';
import {RenderGroup} from '../RenderGroup';
import {Message} from './messages/Message';
import {messageObjectFactory} from './messages/messageObjectFactory';
import {fieldsetCheckboxHandler} from './field-group-change-handlers/fieldsetCheckboxHandler';
import {MagicFieldGroup} from './magic-select/MagicFieldGroup';

/**
 * Represents one configField -- wrapper, label and input.
 *
 * @param props
 * @return {*}
 * @constructor
 */
export const FieldGroup = (props) => {
	if( 'magic' === props.type ){
		return <MagicFieldGroup {...props} />;
	}

	/**
	 * Creates the id attribute
	 * @return {String}
	 */
	function idAttrForHelpElement() {
		return ariaDescribedbyAttr(props.id, props.help);
	}


	/**
	 * Based on type, render the field.
	 *
	 * @return {*}
	 */
	function fieldInner(fieldProps) {
		const message = 'object' === typeof  fieldProps.message
			? messageObjectFactory(fieldProps.message)
			: messageObjectFactory({message: null, error: false});

		function getFieldClassName(conditionalClassNames) {
			return classNames(
				{
					required: fieldProps.isRequired,
					...conditionalClassNames
				}
			);
		}

		/**
		 * Is this option the checked value?
		 * @param {object}option
		 * @param {String|number|Array} currentValue
		 * @return {boolean}
		 */
		function isCheckedOption(option, currentValue) {
			if (Array.isArray(currentValue)) {
				return currentValue.includes(option.value);
			}
			return option.value === currentValue;
		}

		//Fieldsets are rendered recursively.
		if ('fieldset' === fieldProps.type) {
			return (
				<fieldset
					id={props.id}
				>
					<legend>
						{props.label}
					</legend>
					{props.options.map(option => {


						//Call this same function, as a regular checkbox
						return (
							<React.Fragment
								key={`${props.id}-${option.value}`}
							>
								{fieldInner({
									type: 'input',
									inputType: 'checkbox',
									id: `${props.id}-${option.value}`,
									value: isCheckedOption(option, props.value),
									fieldClassName: getFieldClassName({
										'fieldset-checkbox': true,
									}),
									label: option.label,
									disabled: props.disabled,
									onValueChange: () => fieldsetCheckboxHandler(option, props.values, props.onValueChange),
									onBlur: props.onBlur,
									onFocus: props.onFocus
								})}
							</React.Fragment>
						);
					})}
				</fieldset>
			);
		} else if ('hidden' === fieldProps.inputType) {
			return (
				FieldInner(
					{
						type: props.type,
						id: props.id,
						fieldClassName: getFieldClassName(),
						value: fieldProps.value,
						onValueChange: fieldProps.onValueChange,
						inputType: fieldProps.inputType,
						disabled: props.disabled,
						onBlur: props.onBlur,
						onFocus: props.onFocus
					}
				)
			);
		} else {
			return (
				<div
					className={RenderGroup.classNames.fieldWrapper}
				>
					<FieldGroup.Label id={fieldProps.id} label={fieldProps.label} />

					<Message
						message={message}
					/>
					{FieldInner(
						{
							type: fieldProps.type,
							id: fieldProps.id,
							fieldClassName: getFieldClassName({
								'block-input': fieldProps.isBlockInput
							}),
							help: fieldProps.help,
							value: fieldProps.value,
							onValueChange: fieldProps.onValueChange,
							inputType: fieldProps.inputType,
							options: fieldProps.options,
							disabled: props.disabled,
							onBlur: props.onBlur,
							onFocus: props.onFocus
						}
					)}
					{fieldProps.help &&
						<p
							id={`${idAttrForHelpElement()}`}
							className={'description'}
						>
							{fieldProps.help}
						</p>
					}
				</div>
			);
		}

	}

	/**
	 * Render field group
	 */
	return fieldInner(props);
};

/**
 * The prop type definitions for FieldGroup components
 *
 * @type {{id: (boolean|shim|*), isBlockInput: shim, isRequired: shim, help: shim, label: (boolean|shim|*), type: shim, value: shim, onValueChange: (boolean|shim|*), inputType: shim}}
 */
FieldGroup.propTypes = fieldGroupPropTypes;

/**
 * Default props for FieldGroups
 *
 * @type {{isBlockInput: boolean, isRequired: boolean, help: string}}
 */
FieldGroup.defaultProps = {
	isBlockInput: true,
	isRequired: false,
	help: ''
};

/**
 * Creates the FieldGroup's label component
 *
 * @param {Object} props
 * @return {*}
 * @constructor
 */
FieldGroup.Label = (props) => {
	return (
		<label
			htmlFor={props.id}
		>
			{props.label}
		</label>
	);
};

import {fieldInnerPropTypes} from './propTypes';
import React from 'react';
import {Input} from './input/Input';
import {SelectField} from './select/SelectField';
import {ariaDescribedbyAttr} from './util';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import {RenderGroup} from '../RenderGroup';
/**
 * Creates the field inside of a field group
 *
 * @param {Object} props
 * @returns {*}
 * @constructor
 */
export const FieldInner = (props) => {

	/**
	 * Get the ID for the description element if it will be created
	 *
	 * @return {String|null}
	 */
	function ariaIdAttr() {
		return ariaDescribedbyAttr(props.id, props.help);
	}

	/**
	 * Get the className prop for the
	 *
	 * @return {String}
	 */
	function inputClassName() {
		return classNames([
			props.fieldClassName,
			RenderGroup.classNames.input
		]
		);
	}

	switch( props.type ){
	case 'select':
	case 'dropdown':
		const options = Array.isArray(props.options) ? props.options : [];
		return (
			<SelectField
				id={props.id}
				fieldClassName={inputClassName()}
				ariaDescribedbyAttr={ariaIdAttr()}
				value={props.value}
				onValueChange={props.onValueChange}
				inputType={props.inputType}
				options={options}
				disabled={props.disabled}
				onBlur={props.onBlur}
