	let results = {};
	Object.values( configFields ).forEach(configField => {
		const rules = getValidatorsFromConfigField(configField);
		let thisResult = true;
		if( rules.length ){
			thisResult = checkValidatorsForConfigField(configField,fieldValues);

		}
